import { createSlice, configureStore } from "@reduxjs/toolkit";
const initialState = { holidays: {}, country: "" };
export const holidaySlice = createSlice({
  name: "holidayData",
  initialState,
  reducers: {
    display: (state, payload) => {
      state.holidays = payload.payload.response.holidays;
    },
    holidaysFetched: (state, payload) => {
      state.holidays = payload.payload.response.holidays;
    },
    selectedCountry: (state, payload) => {
      console.log("ðŸš€ ~ file: index.js ~ line 14 ~ payload", payload);

      // state.country = payload.payload.country;
    },
  },
});

const store = configureStore({
  reducer: holidaySlice.reducer,
});

export const fetchHolidays = holidaySlice.actions;

export default store;
